import Image from "next/image";
import Link from "next/link";
import styles from "./CardList.module.css";

const items = [
  {
    link: "/recipes?category=korean",
    imageUrl:
      "data:image/jpeg;base64,",
    value: "한식",
  },
  {
    link: "/recipes?category=western",
    imageUrl:
      "data:image/jpeg;base64,",
    value: "양식",
  },
  {
    link: "/recipes?category=dessert",
    imageUrl:
      "data:image/jpeg;base64,",
    value: "디저트",
  },
];

export default function CardList() {
  return (
    <ul className={styles.cardList}>
      {items.map((item) => {
        return (
          <li key={item.value}>
            <CardItem
              link={item.link}
              imageUrl={item.imageUrl}
              value={item.value}
            />
          </li>
        );
      })}
    </ul>
  );
}

function CardItem({
  link,
  imageUrl,
  value,
}: {
  link: string;
  imageUrl?: string;
  value?: string;
}) {
  return (
    <div className={styles.cardItem}>
      <Link href={link}>
        <div className={styles.imageWrapper}>
          <Image
            alt={value || ""}
            src={imageUrl || ""}
            width={200}
            height={200}
          />
        </div>
        <p className={styles.cardValue}>{value}</p>
      </Link>
    </div>
  );
}
